namespace III.Admin.Controllers
{
    [Area("Admin")]
    public class IotDeviceInfoController : BaseController
    {
        private readonly EIMDBContext _context;
        private readonly AppSettings _appSettings;
        private readonly IUploadService _upload;
        /*private readonly IStringLocalizer<CameraListController> _stringLocalizer;*/
        private readonly IStringLocalizer<IotDeviceInfoController> _stringLocalizer;
        private readonly IStringLocalizer<SharedResources> _sharedResources;

        public IotDeviceInfoController(EIMDBContext context, IUploadService upload, IOptions<AppSettings> appSettings, IStringLocalizer<IotDeviceInfoController> stringLocalizer, IStringLocalizer<SharedResources> sharedResources)
        {
            _context = context;
            _upload = upload;
            _appSettings = appSettings.Value;
            _stringLocalizer = stringLocalizer;
            _sharedResources = sharedResources;
        }
        [Breadcrumb("Thiết bị IOT", AreaName = "Admin", FromAction = "Index", FromController = typeof(DashBoardController))]
        public IActionResult Index()
        {
            ViewData["CrumbDashBoard"] = _sharedResources["COM_CRUMB_DASH_BOARD"];
            return View("Index");
        }

        #region Combo box
        [HttpPost]
        public JsonResult GetDeviceGroup()
        {
            var data = _context.CommonSettings.Where(x => !x.IsDeleted && x.Group.Equals(EnumHelper<IotEnum>.GetDisplayValue(IotEnum.IotGroup)))
                        .Select(x => new { Code = x.CodeSet, Name = x.ValueSet });
            return Json(data);
        }

        [HttpPost]
        public JsonResult GetDeviceType()
        {
            var data = _context.CommonSettings.Where(x => !x.IsDeleted && x.Group.Equals(EnumHelper<IotEnum>.GetDisplayValue(IotEnum.IotType)))
                        .Select(x => new { Code = x.CodeSet, Name = x.ValueSet });
            return Json(data);
        }

        [HttpPost]
        public JsonResult GetDeviceStatus()
        {
            var data = _context.CommonSettings.Where(x => !x.IsDeleted && x.Group.Equals(EnumHelper<IotEnum>.GetDisplayValue(IotEnum.IotStatus)))
                        .Select(x => new { Code = x.CodeSet, Name = x.ValueSet });
            return Json(data);
        }
        #endregion

        #region Index

        [HttpPost]
        public object JTable([FromBody]IotDeviceInfoModel jtablePara)
        {
            int intBeginFor = (jtablePara.CurrentPage - 1) * jtablePara.Length;

            var query = from a in _context.IotDeviceInfos.Where(x => !x.IsDeleted)
                        where (string.IsNullOrEmpty(jtablePara.DeviceGroup) || a.DeviceGroup.Equals(jtablePara.DeviceGroup))
                        && (string.IsNullOrEmpty(jtablePara.DeviceType) || a.DeviceType.Equals(jtablePara.DeviceType))
                        && (string.IsNullOrEmpty(jtablePara.DeviceStatus) || a.DeviceStatus.Equals(jtablePara.DeviceStatus))
                        && (string.IsNullOrEmpty(jtablePara.DeviceName) || (a.DeviceName.Contains(jtablePara.DeviceName) || a.DeviceCode.Contains(jtablePara.DeviceName)))
                        select new
                        {
                            Id = a.ID,
                            a.DeviceCode,
                            a.DeviceName,
                            DeviceGroup = _context.CommonSettings.FirstOrDefault(x => !x.IsDeleted && x.CodeSet.Equals(a.DeviceGroup)).ValueSet ?? "",
                            DeviceType = _context.CommonSettings.FirstOrDefault(x => !x.IsDeleted && x.CodeSet.Equals(a.DeviceType)).ValueSet ?? "",
                            DeviceStatus = _context.CommonSettings.FirstOrDefault(x => !x.IsDeleted && x.CodeSet.Equals(a.DeviceStatus)).ValueSet ?? "",
                            DeviceDesc = !string.IsNullOrEmpty(a.DeviceDesc) ? a.DeviceDesc : "",
                            DeviceImage = !string.IsNullOrEmpty(a.DeviceImage) ? a.DeviceImage : "",
                            DeviceManufacturer = !string.IsNullOrEmpty(a.DeviceManufacturer) ? a.DeviceManufacturer : ""
                        };
            var count = query.Count();
            var data = query.OrderUsingSortExpression(jtablePara.QueryOrderBy).Skip(intBeginFor).Take(jtablePara.Length).ToList();
            var jdata = JTableHelper.JObjectTable(data, jtablePara.Draw, count, "Id", "DeviceCode", "DeviceName", "DeviceGroup", "DeviceType", "DeviceStatus",
                    "DeviceDesc", "DeviceImage", "DeviceManufacturer");
            return Json(jdata);
        }

        [HttpPost]
        public object GetDepartment()
        {
            var data = _context.AdDepartments.Where(x => !x.IsDeleted && x.IsEnabled).Select(x => new { Code = x.DepartmentCode, Name = x.Title }).ToList();
            return Json(data);
        }

        [HttpPost]
        public JsonResult GetItem(int id)
        {
            var getItem = _context.IotDeviceInfos.FirstOrDefault(x => x.ID == id);
            return Json(getItem);
        }

        public JsonResult Insert(IotDeviceInfo data, IFormFile images)
        {
            var msg = new JMessage { Title = "", Error = false };
            try
            {
                var checkDevice = _context.IotDeviceInfos.FirstOrDefault(x => !x.IsDeleted && x.DeviceCode.Equals(data.DeviceCode));
                if (checkDevice != null)
                {
                    msg.Title = "Mã thiết bị đã tồn tại";
                    msg.Error = true;
                    return Json(msg);
                }

                if (images != null)
                {
                    var upload = _upload.UploadImage(images);
                    if (upload.Error)
                    {
                        msg.Error = true;
                        msg.Title = upload.Title;
                        return Json(msg);
                    }
                    else
                    {
                        data.DeviceImage = "/uploads/Images/" + upload.Object.ToString();
                    }
                }

                if (data.DeviceType.Equals(EnumHelper<TypeIotDevice>.GetDisplayValue(TypeIotDevice.TypeCamera)))
                {
                    data.DeviceSvg = "<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 840 859' width='840' height='859'><defs>" +
                                    "<image width='839' height='665' id='img1' href='data:image/png;base64,'/>" +
                                    "</defs>" +
                                    "<style>" +
                                    "tspan { white-space:pre }" +
                                    "</style>" +
                                    "<use id = 'Background' href = '#img1' x = '1' y = '1' />" +
                                    "</svg>";
                }
                else if (data.DeviceType.Equals(EnumHelper<TypeIotDevice>.GetDisplayValue(TypeIotDevice.TypeRfid)))
                {
                    data.DeviceSvg = "<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 512 512' width='512' height='512'><defs>" +
                                    "<image width='400' height='480' id='img1' href='data:image/png;base64,'/>" +
                                    "</defs>" +
                                    "<style>" +
                                    "tspan { white-space:pre }" +
                                    "</style>" +
                                    "<use id = 'Background' href = '#img1' x = '96' y = '16' />" +
                                    "</svg>";
                }
                else if (data.DeviceType.Equals(EnumHelper<TypeIotDevice>.GetDisplayValue(TypeIotDevice.TypeSensor)))
                {
                    data.DeviceSvg = "<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 512 512' width='512' height='512'><defs>" +
                                    "<image width='512' height='348' id='img1' href='data:image/png;base64,'/>" +
                                    "</defs>" +
                                    "<style>" +
                                    "tspan { white-space:pre }" +
                                    "</style>" +
                                    "<use id = 'Background' href = '#img1' x = '0' y = '82' />" +
                                    "</svg>";
                }

                data.DeviceDesc = !string.IsNullOrEmpty(data.DeviceDesc) ? data.DeviceDesc : "";
                data.DeviceManufacturer = !string.IsNullOrEmpty(data.DeviceManufacturer) ? data.DeviceManufacturer : "";
                data.CreatedTime = DateTime.Now;
                data.CreatedBy = ESEIM.AppContext.UserName;

                _context.IotDeviceInfos.Add(data);
                _context.SaveChanges();
                msg.Title = _sharedResources["COM_ADD_SUCCESS"];
            }
            catch (Exception ex)
            {
                msg.Error = true;
                msg.Title = _sharedResources["COM_MSG_ERR"];
            }
            return Json(msg);
        }

        [HttpPost]
        public JsonResult Update(IotDeviceInfo data, IFormFile images)
        {
            var msg = new JMessage { Title = "", Error = false };
            try
            {
                var model = _context.IotDeviceInfos.FirstOrDefault(x => x.DeviceCode.Equals(data.DeviceCode));
                if (model == null)
                {
                    msg.Error = true;
                    msg.Title = _stringLocalizer["Thiết bị không tồn tại"];
                }
                else
                {
                    if (images != null)
                    {
                        var upload = _upload.UploadImage(images);
                        if (upload.Error)
                        {
                            msg.Error = true;
                            msg.Title = upload.Title;
                            return Json(msg);
                        }
                        else
                        {
                            data.DeviceImage = "/uploads/Images/" + upload.Object.ToString();
                        }
                    }
                    else
                    {
                        data.DeviceImage = data.DeviceImage;
                    }

                    if (data.DeviceType.Equals(EnumHelper<TypeIotDevice>.GetDisplayValue(TypeIotDevice.TypeCamera)))
                    {
                        data.DeviceSvg = "<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 840 859' width='840' height='859'><defs>" +
                                    "<image width='839' height='665' id='img1' href='data:image/png;base64,'/>" +
                                    "</defs>" +
                                    "<style>" +
                                    "tspan { white-space:pre }" +
                                    "</style>" +
                                    "<use id = 'Background' href = '#img1' x = '1' y = '1' />" +
                                    "</svg>";
                    }
                    else if (data.DeviceType.Equals(EnumHelper<TypeIotDevice>.GetDisplayValue(TypeIotDevice.TypeRfid)))
                    {
                        data.DeviceSvg = "<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 512 512' width='512' height='512'><defs>" +
                                    "<image width='400' height='480' id='img1' href='data:image/png;base64,'/>" +
                                    "</defs>" +
                                    "<style>" +
                                    "tspan { white-space:pre }" +
                                    "</style>" +
                                    "<use id = 'Background' href = '#img1' x = '96' y = '16' />" +
                                    "</svg>";
                    }
                    else if (data.DeviceType.Equals(EnumHelper<TypeIotDevice>.GetDisplayValue(TypeIotDevice.TypeSensor)))
                    {
                        data.DeviceSvg = "<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 512 512' width='512' height='512'><defs>" +
                                    "<image width='512' height='348' id='img1' href='data:image/png;base64,'/>" +
                                    "</defs>" +
                                    "<style>" +
                                    "tspan { white-space:pre }" +
                                    "</style>" +
                                    "<use id = 'Background' href = '#img1' x = '0' y = '82' />" +
                                    "</svg>";
                    }

                    model.DeviceName = data.DeviceName;
                    model.DeviceImage = data.DeviceImage;
                    model.UpdatedBy = ESEIM.AppContext.UserName;
                    model.UpdatedTime = DateTime.Now;
                    model.DeviceGroup = data.DeviceGroup;
                    model.DeviceType = data.DeviceType;
                    model.DeviceStatus = data.DeviceStatus;
                    model.DeviceDesc = !string.IsNullOrEmpty(data.DeviceDesc) ? data.DeviceDesc : "";
                    model.DeviceManufacturer = !string.IsNullOrEmpty(data.DeviceManufacturer) ? data.DeviceManufacturer : "";
                    model.DeviceSvg = data.DeviceSvg;

                    _context.IotDeviceInfos.Update(model);
                    _context.SaveChanges();
                    msg.Title = _sharedResources["COM_UPDATE_SUCCESS"];
                }
            }
            catch (Exception ex)
            {
                msg.Error = true;
                msg.Title = _sharedResources["COM_ERR_ADD"];
            }
            return Json(msg);
        }

        [HttpPost]
        public JsonResult Delete(int id)
        {
            var msg = new JMessage { Error = false, Title = "" };
            try
            {
                var data = _context.IotDeviceInfos.FirstOrDefault(x => x.ID == id);
                if (data != null)
                {
                    data.IsDeleted = true;
                    data.DeletedBy = ESEIM.AppContext.UserName;
                    data.DeletedTime = DateTime.Now;

                    _context.IotDeviceInfos.Update(data);
                    _context.SaveChanges();

                    msg.Title = _sharedResources["COM_DELETE_SUCCESS"];
                }
                else
                {
                    msg.Error = true;
                    msg.Title = "Thiết bị không tồn tại";
                }
            }
            catch (Exception ex)
            {
                msg.Title = _sharedResources["COM_MSG_ERR"];
                msg.Error = true;
            }
            return Json(msg);
        }

        public class IotDeviceInfoModel : JTableModel
        {
            public string DeviceName { get; set; }
            public string DeviceGroup { get; set; }
            public string DeviceType { get; set; }
            public string DeviceStatus { get; set; }
        }

        #endregion

        #region Language
        [HttpGet]
        public IActionResult Translation(string lang)
        {
            var resourceObject = new JObject();
            var query = _stringLocalizer.GetAllStrings().Select(x => new { x.Name, x.Value }).Union(_sharedResources.GetAllStrings().Select(x => new { x.Name, x.Value }));
            foreach (var item in query)
            {
                resourceObject.Add(item.Name, item.Value);
            }
            return Ok(resourceObject);
        }
        #endregion
    }
}
